-- #3USE AP;SELECT (VendorContactLName + ','+ VendorContactFName) AS 'Full Name' 	FROM VendorsORDER BY VendorContactLName, VendorContactFName;-- #6SELECT (VendorContactLName + ','+ VendorContactFName) AS 'Full Name' 	FROM VendorsWHERE VendorContactLName LIKE '[A,B,C,E]%'ORDER BY VendorContactLName, VendorContactFName;-- #7--Write a SELECT statement that determines whether the--PaymentDate column of the Invoices table has any invalid values.--To be valid, PaymentDate must be a null value if there’s a balance due--and a non-null value if there’s no balance due.--Code a compound condition in the WHERE clause that tests for these conditions.SELECT InvoiceNumber, PaymentDate, (InvoiceTotal - PaymentTotal - CreditTotal) AS BalanceDue	FROM InvoicesWHERE (PaymentDate IS NULL	AND (InvoiceTotal - PaymentTotal - CreditTotal) <= 0)	OR (PaymentDate IS NOT NULL	AND (InvoiceTotal - PaymentTotal - CreditTotal) > 0);